// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )
def fournisseur = "SNCF-ACCES"
// define namespace
holder.namespaces["siri"] = "http://www.siri.org.uk/siri"
// récupération de l'id message de la request
requestContext.idMessage = holder["//siri:MessageIdentifier"]
requestContext.monitoringRef1 = holder["//siri:MonitoringRef"]
requestContext.version = holder["//Request/@version"].toString()

// identifiant utilisé (en coherence avec le dataset redis)
requestContext.producerRef="SNCF-ACCES"
requestContext.recordedAtTime = javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(GregorianCalendar.getInstance())
requestContext.lineRef="STIF:Line::004004009"
requestContext.dataFrameRef="any"
requestContext.datedVehicleJourneyRef=fournisseur+":VehicleJourney::1:LOC"
requestContext.journeyPatternRef=fournisseur+":JourneyPattern::1:LOC"
requestContext.journeyPatternName="mission1"
requestContext.publishedLineName="N"
requestContext.routeRef=fournisseur+":Route::1:LOC"
requestContext.directionName="Rambouillet"
requestContext.operatorRef=fournisseur+":Operator::SNCF:"
requestContext.originRef="STIF:StopPoint:Q:108935:"
requestContext.originName="Arrêt 1"
requestContext.placeRef="STIF:StopPoint:Q:108943:"
requestContext.placeName="Arrêt 5"
requestContext.destinationRef="STIF:StopPoint:Q:108949:"
requestContext.destinationName="Arrêt 10"
requestContext.vehicleJourneyName="Course 1"
// heure de départ au 1er arrêt et dernier arrêt de la course
def heureDepartCourse = getHeureSuivante(1)
requestContext.originAimedDepartureTime=heureDepartCourse.toXMLFormat();
requestContext.destinationAimedArrivalTime=getAvecMinuteSeconde(heureDepartCourse,20,0)
// Passage 1 / Arrêt 1
requestContext.itemIdenfier1=fournisseur+":Item::1:LOC"
//MonitoredCall
requestContext.stopPointRef1=requestContext.monitoringRef1
requestContext.stopPointName1="Arrêt 1"

// passage 2 / Arrêt 1
requestContext.itemIdenfier2=fournisseur+":Item::2:LOC"

// Passage 3 / arrêt 2
requestContext.monitoringRef2 = holder["//siri:MonitoringRef"]
requestContext.stopPointRef2=requestContext.monitoringRef2
requestContext.stopPointName2="Arrêt 2"
requestContext.itemIdenfier3=fournisseur+":Item::3:LOC"
//MonitoredCall

// passage 4 / arrêt 2
